func BytesToInt(b []byte) int {
	bytesBuffer := bytes.NewBuffer(b)

	var x int32
	binary.Read(bytesBuffer, binary.BigEndian, &x)

	return int(x)
}

func IntToBytes(n int) []byte {
	x := int32(n)
	bytesBuffer := bytes.NewBuffer([]byte{})
	binary.Write(bytesBuffer, binary.BigEndian, x)
	return bytesBuffer.Bytes()
}

func Test_erc721_interfaceId(t *testing.T) {

	nor := BytesToInt(evmFuncHashBytes("balanceOf(address)")) ^
		BytesToInt(evmFuncHashBytes("ownerOf(uint256)")) ^
		BytesToInt(evmFuncHashBytes("approve(address,uint256)")) ^
		BytesToInt(evmFuncHashBytes("getApproved(uint256)")) ^
		BytesToInt(evmFuncHashBytes("setApprovalForAll(address,bool)")) ^
		BytesToInt(evmFuncHashBytes("isApprovedForAll(address,address)")) ^
		BytesToInt(evmFuncHashBytes("transferFrom(address,address,uint256)")) ^
		BytesToInt(evmFuncHashBytes("safeTransferFrom(address,address,uint256)")) ^
		BytesToInt(evmFuncHashBytes("safeTransferFrom(address,address,uint256,bytes)"))

	norBytes := IntToBytes(nor)
	//80ac58cd
	// InterfaceId_ERC721 = 0x80ac58cd
	fmt.Println("InterfaceId_ERC721 =", hex.EncodeToString(norBytes))

}

